.app {
  height: auto;
  width: calc(var(--appsmith-ui-width) * 1px - 20px);
  justify-content: center;
  border-radius: var(--appsmith-theme-borderRadius);
  box-shadow: var(--appsmith-theme-boxShadow);
  padding: 29px 25px;
  box-sizing: border-box;
  font-family: system-ui;
  background: #fff;
  padding-bottom: 100px;
  overflow-x: none;
  position: relative;
  overflow-y: scroll;
}

* {
  box-sizing: border-box;
  font-family: Arial, sans-serif;
}

.gallery-container {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(100px, 1fr));
  gap: 10px;
  padding: 10px;
  overflow-x: none;
  width: 100%;
}

.gallery-item {
  position: relative;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
}

.gallery-item img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: absolute;
  border-radius: 8px;
}

.remove-btn {
  position: absolute;
  bottom: 5px;
  right: 5px;
  border: none;
  padding: 8px 15px;
  cursor: pointer;
  border-radius: 5px;
  font-size: 12px;
  font-weight: bold;
  color: white;
  background: #ff7b7b;
  transition: all 0.2s;
  opacity: 0;
}

.remove-btn:hover {
  background: #a70000;
}

.gallery-item:hover .remove-btn {
  opacity: 1;
}

