function renderGallery() {
  const imageData = appsmith.model.data || [];
  if (imageData.length === 0) return;
  const gallery = document.getElementById("gallery");
  gallery.innerHTML = ""; // Limpa antes de renderizar

  imageData.forEach(image => {
    if (image.type != "video/mp4") {
      const item = document.createElement("div");
      item.classList.add("gallery-item");
      item.onclick = () => {
        window.open(image.url, "_blank");
      };
      const img = document.createElement("img");
      img.src = image.url;
      img.alt = image.filename;
      const btn = document.createElement("button");
      btn.innerText = "Remover";
      btn.classList.add("remove-btn");
      btn.onclick = () => {
        event.stopPropagation();
        removeImage(image.id);
      };
      item.appendChild(img);
      item.appendChild(btn);
      gallery.appendChild(item);
    }
  });
}
function removeImage(id) {
  const imageData = appsmith.model.data;
  const image = imageData.find(image => image.id === id);
  appsmith.updateModel({
    image
  });
  appsmith.triggerEvent('removeButtonClick');
  renderGallery();
}
function initApp() {
  renderGallery();
}
appsmith.onModelChange(() => {
  renderGallery();
});
appsmith.onReady(() => {
  /*
   * This handler function will get called when parent application is ready.
   * Initialize your component here
   * more info - https://docs.appsmith.com/reference/widgets/custom#onready
   */
  initApp();
});

